{{ ((chartType) => ( 
(() => {
  const rows = statpull.data || [];

  // --- Clean + tokenize: supports commas/semicolons/pipes and quoted phrases ---
  const raw = (Input1?.text || "")
    .toLowerCase()
    .replace(/\u200b|\u200c|\u200d|\ufeff/g, ""); // strip zero-widths/BOMs

  function tokenize(s) {
    const seps = new Set([",",";","|"]);
    const out = [];
    let cur = "";
    let inQ = false, q = "";
    for (let i = 0; i < s.length; i++) {
      const ch = s[i];
      if (ch === "'" || ch === '"') {
        if (!inQ) { inQ = true; q = ch; }
        else if (q === ch) { inQ = false; q = ""; }
        else { cur += ch; }
      } else if (seps.has(ch) && !inQ) {
        const t = cur.trim();
        if (t) out.push(t); // ignore empties (trailing comma safe)
        cur = "";
      } else {
        cur += ch;
      }
    }
    const tail = cur.trim();
    if (tail) out.push(tail);
    return out;
  }

  const terms = tokenize(raw);

  // ANY-token match; empty terms => show all (so dangling comma won't blank chart)
  const matchesSearch = (seriesName) => {
    if (terms.length === 0) return true;
    const name = seriesName.toLowerCase();
    return terms.some(t => name.includes(t));
  };

  // --- Step 1: Build sorted list of unique dates ---
  const dates = [...new Set(rows.map(r => r.collection_date))].sort();

  // --- Step 2: Group data by service_id and date ---
  const bySvc = {};
  for (const r of rows) {
    if (!bySvc[r.service_id]) {
      bySvc[r.service_id] = {
        label: `${r.external_id} -- ${r.description}`,
        byDate: {}
      };
    }

    const sum24 = (pfx) =>
      Array.from({ length: 24 }, (_, i) => Number(r[`${pfx}_${i}`] ?? 0))
        .reduce((a, b) => a + b, 0);

    // Find peak hour for the day
    let peakVal = 0;
    for (let h = 0; h < 24; h++) {
      const inboundH = Number(r[`inbound_${h}`] ?? 0);
      const outboundH = Number(r[`outbound_${h}`] ?? 0);
      const totalH = inboundH + outboundH;
      if (totalH > peakVal) peakVal = totalH;
    }

    bySvc[r.service_id].byDate[r.collection_date] = {
      inbound: sum24('inbound'),
      outbound: sum24('outbound'),
      peak: peakVal
    };
  }

  // --- Step 3: Build series ---
  const series = [];
  for (const svc of Object.values(bySvc)) {
    const inbound = dates.map(d => svc.byDate[d]?.inbound ?? 0);
    const outbound = dates.map(d => svc.byDate[d]?.outbound ?? 0);
    const peak = dates.map(d => svc.byDate[d]?.peak ?? 0);

    const inboundName = `${svc.label} — inbound`;
    const outboundName = `${svc.label} — outbound`;
    const peakName = `${svc.label} — peak`;

    if (matchesSearch(inboundName)) {
      series.push({
        name: inboundName,
        type: 'line',
        smooth: false,
        areaStyle: {},
        stack: 'total',
        data: inbound,
        showSymbol: true,
        showAllSymbol: true,
        symbolSize: 10
      });
    }

    if (matchesSearch(outboundName)) {
      series.push({
        name: outboundName,
        type: 'line',
        smooth: false,
        areaStyle: {},
        stack: 'total',
        data: outbound,
        showSymbol: true,
        showAllSymbol: true,
        symbolSize: 10
      });
    }

    if (matchesSearch(peakName)) {
      series.push({
        name: peakName,
        type: 'bar',
        stack: 'peakstack',
        barWidth: 50,
        data: peak
      });
    }
  }

  // Sort legend alphabetically
  series.sort((a, b) => a.name.localeCompare(b.name));

  // --- Step 4: Tooltip mode ---
  const tooltipMode = SwitchHoverItemOnly?.isSwitchedOn ? 'item' : 'axis';

  return {
    title: { text: 'Daily Calls - Last 30 Days' },
    tooltip: {
      trigger: tooltipMode,
      axisPointer: { type: 'shadow' },
      formatter: !SwitchHoverItemOnly?.isSwitchedOn
        ? function (params) {
            const date = params?.[0]?.axisValueLabel ?? '';
            return `<b>${date}</b><br/>` + params
              .map(p => `${p.marker} ${p.seriesName}: ${p.value}`)
              .join('<br/>');
          }
        : undefined
    },
    legend: {
      type: 'scroll',
      orient: 'vertical',
      right: 1,
      top: 'top',
      align: 'right',
      selectedMode: 'multiple',
      selector: [
        { type: 'all', title: 'Show All' },
        { type: 'inverse', title: 'Single View' }
      ]
    },
    emphasis: { focus: 'series', blurScope: 'coordinateSystem' },
    grid: { left: 60, right: 180, top: 50, bottom: 40 },
    xAxis: { type: 'category', data: dates },
    yAxis: { type: 'value', name: 'Calls/day' },
    series
  };
})()
))(dayStats.chartType) }}
