{{ ((chartType) => ( 
(() => {
  const rows = statpull.data || [];

  // --- Toggle detection (safe default) ---
  const hoverItemOnly = (() => {
    try { return !!SwitchHoverItemOnly.isSwitchedOn; } catch (_) { return false; }
  })();

  // --- Clean + tokenize Input1 text ---
  const raw = (Input1?.text || "")
    .toLowerCase()
    .replace(/\u200b|\u200c|\u200d|\ufeff/g, ""); // strip zero-widths/BOMs

  function tokenize(s) {
    const seps = new Set([",",";","|"]);
    const out = [];
    let cur = "";
    let inQ = false, q = "";
    for (let i = 0; i < s.length; i++) {
      const ch = s[i];
      if (ch === "'" || ch === '"') {
        if (!inQ) { inQ = true; q = ch; }
        else if (q === ch) { inQ = false; q = ""; }
        else { cur += ch; }
      } else if (seps.has(ch) && !inQ) {
        const t = cur.trim();
        if (t) out.push(t); // ignore empties (trailing sep safe)
        cur = "";
      } else {
        cur += ch;
      }
    }
    const tail = cur.trim();
    if (tail) out.push(tail);
    return out;
  }

  const terms = tokenize(raw);

  // ANY-token match; empty terms => show all
  const matchesTokens = (str) => {
    if (terms.length === 0) return true;
    const name = (str || "").toLowerCase();
    return terms.some(t => name.includes(t));
  };

  // --- Picked date (or latest available) ---
  const pick = DatePicker1?.selectedDate;
  const latest = (rows.map(r => r.collection_date).filter(Boolean).sort().pop());
  const pickDay = pick
    ? new Date(pick).toISOString().slice(0, 10)
    : latest;

  // --- Filter by day and tokens ---
  const dayRows = rows.filter(r => {
    if (!pickDay || r.collection_date !== pickDay) return false;
    const label = `${r.external_id} — ${r.description}`;
    return matchesTokens(label);
  });

  // --- Hour labels 12AM..11PM ---
  const hourLabels = Array.from({ length: 24 }, (_, h) => {
    const hr = h % 12 === 0 ? 12 : h % 12;
    const ampm = h < 12 ? 'AM' : 'PM';
    return `${hr}${ampm}`;
  });

  // --- Build series: per service two lines (inbound/outbound) ---
  const series = [];
  for (const r of dayRows) {
    const label = `${r.external_id} -- ${r.description}`;
    const inbound = Array.from({ length: 24 }, (_, h) => Number(r[`inbound_${h}`] ?? 0));
    const outbound = Array.from({ length: 24 }, (_, h) => Number(r[`outbound_${h}`] ?? 0));

    series.push({
      name: `${label} — inbound`,
      type: 'line',
      stack: 'total',
      areaStyle: {},
      smooth: false,
      showSymbol: true,
      showAllSymbol: true,
      symbolSize: 6,
      data: inbound,
    });
    series.push({
      name: `${label} — outbound`,
      type: 'line',
      stack: 'total',
      areaStyle: {},
      smooth: false,
      showSymbol: true,
      showAllSymbol: true,
      symbolSize: 6,
      data: outbound,
    });
  }

  return {
    title: { text: `Hourly Calls (${pickDay || 'no date'})` },
    tooltip: {
      trigger: hoverItemOnly ? 'item' : 'axis',
      axisPointer: { type: 'line' },
      formatter: hoverItemOnly
        ? undefined
        : function (params) {
            const hour = params?.[0]?.axisValueLabel ?? '';
            return `<b>${hour}</b><br/>` + params
              .map(p => `${p.marker} ${p.seriesName}: ${p.value}`)
              .join('<br/>');
          }
    },
    legend: {
      type: 'scroll',
      orient: 'vertical',
      right: 2,
      top: 'top',
      align: 'right',
      selectedMode: 'multiple',
      selector: [
        { type: 'all', title: 'Show All' },
        { type: 'inverse', title: 'Single View' }
      ],
      tooltip: { show: true }
    },
    emphasis: { focus: 'series', blurScope: 'coordinateSystem' },
    grid: { left: 50, right: 20, top: 50, bottom: 40 },
    xAxis: { type: 'category', data: hourLabels },
    yAxis: { type: 'value', name: 'Calls per hour' },
    series,
  };
})()
))(hourStats.chartType) }}
