{{ ((chartType) => ( 
(() => {
  const rows = statpull.data || [];
  const sel = Select1?.selectedOptionValue;

  // filter by selected service_id (or show all if empty/ALL)
  const filtered = rows.filter(r => !sel || sel === 'ALL' || r.service_id === sel);

  // unique, sorted dates for x-axis
  const dates = [...new Set(filtered.map(r => r.collection_date))].sort();

  // group by service_id; keep a nice label
  const bySvc = {};
  for (const r of filtered) {
    if (!bySvc[r.service_id]) {
      bySvc[r.service_id] = {
        label: `${r.external_id} — ${r.description} - ${r.service_id}`,
        byDate: {}
      };
    }
    const sum24 = (pfx) =>
      Array.from({ length: 24 }, (_, i) => Number(r[`${pfx}_${i}`] ?? 0))
        .reduce((a, b) => a + b, 0);

    bySvc[r.service_id].byDate[r.collection_date] = {
      inbound: sum24('inbound'),
      outbound: sum24('outbound'),
    };
  }

  // build series per service (two lines each)
  const series = [];
  for (const [svcId, obj] of Object.entries(bySvc)) {
    const inbound = dates.map(d => obj.byDate[d]?.inbound ?? 0);
    const outbound = dates.map(d => obj.byDate[d]?.outbound ?? 0);

    series.push({
      name: `${obj.label} — inbound`,
      type: 'line',
      smooth: false,
      data: inbound,
    });
    series.push({
      name: `${obj.label} — outbound`,
      type: 'line',
      smooth: false,
      data: outbound,
    });
  }

  return {
    tooltip: { trigger: 'axis' },
    legend: {
    type: 'scroll',        // scroll if lots of services
    orient: 'vertical',    // stack vertically
    right: 2,             // distance from right edge
    top: 'middle',         // vertical centering
    align: 'right',         // labels align right of swatches
    selectedMode: 'single',
    selector: [
      {type: 'all', title: 'Show All' },
      {type: 'inverse', title: 'Single View' }
    ],
    tooltip: { show: true } // shows tooltip when you hover legend items
    },
    tooltip: {
    trigger: 'axis',
    axisPointer: { type: 'line' }
    },
    emphasis: {
    focus: 'series',   // highlight series + legend when hovering point/line
    blurScope: 'coordinateSystem' // fade others in same chart
    },
    toolbox: {
        feature: {
        dataZoom: {
            yAxisIndex: 'none'
        },
        restore: {},
        saveAsImage: {}
        }
    },   
    xAxis: { type: 'category', data: dates },
    yAxis: { type: 'value', name: 'Calls/day' },
    series,
  };
})()
))(Chart1Copy1.chartType) }}
