{{ ((chartType) => ( 
(() => {
  const search = String(eomDevices.searchText || "").toLowerCase();
  const base = Array.isArray(getEOMStats.data) ? getEOMStats.data : [];

  // contains match on name
  const bySearch = base.filter(d =>
    !search || String(d?.name || "").toLowerCase().includes(search)
  );

  // ignore peak_*; drop rows where recent_ingress + recent_egress == 0 (or missing)
  const nonZero = bySearch.filter(d => {
    const inVal = Number(d?.recent_ingress ?? 0);
    const egVal = Number(d?.recent_egress ?? 0);
    return (inVal + egVal) > 0;
  });

  // sort Z → A by name (flip to a.name.localeCompare(b.name) for A→Z)
  const sorted = [...nonZero].sort((a, b) => b.name.localeCompare(a.name));

  return {
    tooltip: { trigger: "axis", axisPointer: { type: "shadow" } },
    legend: { data: ["Ingress", "Egress"] },
    xAxis: { type: "value", name: "Count" },
    yAxis: { type: "category", data: sorted.map(d => d.name) },
    series: [
      {
        name: "Ingress",
        type: "bar",
        stack: "total",
        emphasis: { focus: "series" },
        data: sorted.map(d => Number(d.recent_ingress || 0))
      },
      {
        name: "Egress",
        type: "bar",
        stack: "total",
        emphasis: { focus: "series" },
        data: sorted.map(d => Number(d.recent_egress || 0))
      }
    ]
  };
})()
))(Chart2.chartType) }}
