{{ ((chartType) => ( 
(() => {
  const rows = statpull.data || [];
  const txt = (Input1?.text || "").toLowerCase();

  // dates for x-axis
  const dates = [...new Set(rows.map(r => r.collection_date))].sort();

  // rollup per service_id
  const bySvc = {};
  for (const r of rows) {
    if (!bySvc[r.service_id]) {
      bySvc[r.service_id] = {
        label: `${r.external_id} -- ${r.description}`,
        byDate: {}
      };
    }

    const sum24 = (pfx) =>
      Array.from({ length: 24 }, (_, i) => Number(r[`${pfx}_${i}`] ?? 0))
        .reduce((a, b) => a + b, 0);

    let peakVal = 0;
    for (let h = 0; h < 24; h++) {
      const inboundH = Number(r[`inbound_${h}`] ?? 0);
      const outboundH = Number(r[`outbound_${h}`] ?? 0);
      const totalH = inboundH + outboundH;
      if (totalH > peakVal) peakVal = totalH;
    }

    bySvc[r.service_id].byDate[r.collection_date] = {
      inbound: sum24('inbound'),
      outbound: sum24('outbound'),
      peak: peakVal
    };
  }

  // helper: does this series match the search text?
  const matchesSearch = (seriesName) => {
    if (!txt) return true;
    if (seriesName.toLowerCase().includes(txt)) return true;
    return false;
  };

  // build series
  const series = [];
  for (const svc of Object.values(bySvc)) {
    const inbound = dates.map(d => svc.byDate[d]?.inbound ?? 0);
    const outbound = dates.map(d => svc.byDate[d]?.outbound ?? 0);
    const peak = dates.map(d => svc.byDate[d]?.peak ?? 0);

    const inboundName = `${svc.label} — inbound`;
    const outboundName = `${svc.label} — outbound`;
    const peakName = `${svc.label} — peak`;

    if (matchesSearch(inboundName)) {
      series.push({
        name: inboundName,
        type: 'line',
        smooth: true,
        areaStyle: {},
        stack: 'total',
        data: inbound
      });
    }

    if (matchesSearch(outboundName)) {
      series.push({
        name: outboundName,
        type: 'line',
        smooth: true,
        areaStyle: {},
        stack: 'total',
        data: outbound
      });
    }

    if (matchesSearch(peakName)) {
      series.push({
        name: peakName,
        type: 'bar',
        stack: 'peakstack',
        barWidth: 50,
        data: peak
      });
    }
  }

  // sort legend alphabetically
  series.sort((a, b) => a.name.localeCompare(b.name));

  return {
    title: { text: 'Daily Calls (Inbound/Outbound Bars + Peak Line)' },
    tooltip: { trigger: 'axis', axisPointer: { type: 'shadow' } },
    legend: {
      type: 'scroll',
      orient: 'vertical',
      right: 10,
      top: 'middle',
      align: 'left',
      selectedMode: 'multiple',
      selector: [
        { type: 'all', title: 'Show All' },
        { type: 'inverse', title: 'Single View' }
      ]
    },
    emphasis: { focus: 'series', blurScope: 'coordinateSystem' },
    grid: { left: 60, right: 180, top: 50, bottom: 40 },
    xAxis: { type: 'category', data: dates },
    yAxis: { type: 'value', name: 'Calls/day' },
    series
  };
})()
))(dayStats.chartType) }}
