{{
  (() => {
    // ============================================================================
    // FUNCTION 1: GET SELECTED CUSTOMER
    // ============================================================================
    const selectedAccountNo = appsmith.store.statclickaccount;

    // ============================================================================
    // FUNCTION 2: DEFAULT DIAGRAM - NO SELECTION
    // ============================================================================
    if (!selectedAccountNo) {
      return `flowchart TD
    msg["Select a customer
to view network diagram"]
    style msg fill:#f0f0f0,stroke:#999,stroke-width:2px,color:#666`;
    }

    // ============================================================================
    // FUNCTION 3: FIND CUSTOMER DATA
    // ============================================================================
    const customer = manipTable.tableData.find(c => c.account_no === selectedAccountNo);

    if (!customer) {
      return `flowchart TD
    msg["Account ${selectedAccountNo}

No Gen1 or IVP
connections found"]
    style msg fill:#fff3cd,stroke:#ffc107,stroke-width:2px,color:#856404`;
    }

    // ============================================================================
    // FUNCTION 4: UTILITY FUNCTIONS
    // ============================================================================
    const parseJsonField = (field) => {
      if (!field || field === 'null' || field === '[]') return [];
      try {
        return JSON.parse(field);
      } catch {
        return [];
      }
    };

    const splitConcatenatedField = (field) => {
      if (!field || field === 'null' || field === 'N/A') return [];
      return field.split(', ').map(s => s.trim());
    };

    // ============================================================================
    // FUNCTION 5: EXTRACT CUSTOMER METADATA
    // ============================================================================
    let carriers = customer.carrier_connections || "Carrier";
    const usesSema = customer.uses_sema_payments === true;
    const hasBackbone = customer.gen1_ivp_backbone === true;
    const customerName = customer.customer;
    const accountNo = customer.account_no;

    // ============================================================================
    // FUNCTION 6: CHECK FOR GEN1 CONFIGURATION
    // ============================================================================
    const sourceRealm = customer.source_realm || "";
    const hasGen1Realm = sourceRealm !== "" && sourceRealm !== null && sourceRealm !== "null";

    const hasLocalPolicyData = customer.local_policy_summary && 
      customer.local_policy_summary !== 'null' && 
      customer.local_policy_summary.trim() !== '';

    const hasGen1DataSource = customer.data_source && 
      (customer.data_source.startsWith('SYD1') || customer.data_source.startsWith('SYD2'));

    const showGen1Customer = hasGen1Realm || hasLocalPolicyData || hasGen1DataSource;

    // ============================================================================
    // FUNCTION 7: FIND SYD1/SYD2 SPECIFIC DATA (GEN1 ONLY)
    // ============================================================================
    const syd1Data = manipTable.tableData.find(c => 
      c.account_no === selectedAccountNo && c.data_source && c.data_source.startsWith('SYD1')
    );
    const syd2Data = manipTable.tableData.find(c => 
      c.account_no === selectedAccountNo && c.data_source && c.data_source.startsWith('SYD2')
    );

    // ============================================================================
    // FUNCTION 8: BUILD SESSION AGENT INFO (GEN1 ONLY)
    // ============================================================================
    const buildSessionAgentInfo = (data) => {
      if (!data) return "N/A";

      const realm = data.source_realm || "N/A";
      const sessionAgentsRaw = data.customer_realm_session_agents || "N/A";
      const sessionAgents = sessionAgentsRaw === "N/A" ? "N/A" : 
        sessionAgentsRaw.split(',').map(s => s.trim()).join('<br/>');

      const sipIpsRaw = data.sip_interface_ips || "N/A";
      const sipIps = sipIpsRaw === "N/A" ? "N/A" : 
        sipIpsRaw.split(',').map(s => s.trim()).join('<br/>');

      return `Realm: ${realm}<br/>Agents:<br/>${sessionAgents}<br/>SIP IPs:<br/>${sipIps}`;
    };

    const sessagen1 = buildSessionAgentInfo(syd1Data);
    const sessagen2 = buildSessionAgentInfo(syd2Data);

    // ============================================================================
    // FUNCTION 9: GET FIRST IVP DATA ROW (IDEMPOTENT - DEDUPLICATE)
    // ============================================================================
    // Find first row with IVP services to avoid duplicates
    const allCustomerRows = manipTable.tableData.filter(c => c.account_no === selectedAccountNo);
    const firstIvpRow = allCustomerRows.find(row => {
      const sipSvcs = parseJsonField(row.ivp_svcSIP);
      const voiceGws = parseJsonField(row.ivp_svcVoiceGateway);
      return sipSvcs.length > 0 || voiceGws.length > 0;
    });

    // ============================================================================
    // FUNCTION 10: BUILD IVP SERVICE MAPPING TABLE
    // ============================================================================
    const buildIvpServiceTable = (data) => {
      if (!data) return { serviceTable: [], carriages: [] };

      const sipSvcs = parseJsonField(data.ivp_svcSIP);
      const voiceGws = parseJsonField(data.ivp_svcVoiceGateway);
      const carriages = parseJsonField(data.ivp_svccarriages);

      const regionNames = splitConcatenatedField(data["Region Name"]);
      const ivpNames = splitConcatenatedField(data.IVPName);
      const routingDomains = splitConcatenatedField(data["Routing Domain"]);
      const inboundUris = splitConcatenatedField(data["Inbound server/uri"]);

      const serviceTable = [];

      // Process SIP services
      sipSvcs.forEach((svc, idx) => {
        serviceTable.push({
          type: 'SIP',
          regionName: regionNames[idx] || 'AU1',
          ivpName: ivpNames[idx] || 'N/A',
          routingDomain: routingDomains[idx] || 'N/A',
          inboundUri: inboundUris[idx] || 'N/A',
          serviceData: svc,
          index: idx
        });
      });

      // Process Voice Gateway services
      voiceGws.forEach((gw, idx) => {
        const serviceIdx = sipSvcs.length + idx;
        serviceTable.push({
          type: 'VoiceGateway',
          regionName: regionNames[serviceIdx] || 'AU1',
          ivpName: ivpNames[serviceIdx] || 'N/A',
          routingDomain: routingDomains[serviceIdx] || 'N/A',
          inboundUri: inboundUris[serviceIdx] || 'N/A',
          serviceData: gw,
          index: serviceIdx
        });
      });

      return { serviceTable, carriages };
    };

    const ivpData = buildIvpServiceTable(firstIvpRow);
    const hasAnyIvpServices = ivpData.serviceTable.length > 0;
    const hasIvpCarriages = ivpData.carriages.length > 0;

    // ============================================================================
    // FUNCTION 11: VALIDATE CUSTOMER HAS CONNECTIONS
    // ============================================================================
    if (!hasGen1Realm && !hasAnyIvpServices) {
      return `flowchart TD
    msg["${customerName}
(${accountNo})

No Gen1 or IVP
connections found"]
    style msg fill:#fff3cd,stroke:#ffc107,stroke-width:2px,color:#856404`;
    }

    // ============================================================================
    // FUNCTION 12: BUILD IVP INSTANCES AND CONNECTIONS
    // ============================================================================
    const buildIvpComponents = (serviceTable) => {
      if (serviceTable.length === 0) {
        return {
          ivpInstances: new Set(),
          connections: [],
          serviceBoxes: []
        };
      }

      // Extract unique region names to build IVP instances
      const ivpInstances = new Set();
      serviceTable.forEach(svc => {
        ivpInstances.add(svc.regionName);
      });

      // Count services per IVP instance for sequential numbering
      const instanceCounters = {};
      ivpInstances.forEach(region => {
        instanceCounters[region] = 0;
      });

      // Build connections and service boxes
      const connections = [];
      const serviceBoxes = [];

      serviceTable.forEach(svc => {
        const region = svc.regionName;
        instanceCounters[region]++;
        const seqNum = instanceCounters[region];

        // Create service node ID
        const serviceNodeId = `service_inbound_${region}_${seqNum}`;

        // Create connection from IVP to service
        const connId = `ivpConnect_${region}_${seqNum}`;
        connections.push({
          from: `IVP${region}`,
          to: serviceNodeId,
          label: svc.routingDomain,
          id: connId
        });

        // Create service box
        const serviceLabel = svc.type === 'SIP' 
          ? `${svc.ivpName}<br/>${svc.serviceData.profile.name}<br/>Inbound: ${svc.inboundUri}`
          : `${svc.ivpName}<br/>Inbound: ${svc.inboundUri}`;

        serviceBoxes.push({
          id: serviceNodeId,
          label: serviceLabel
        });
      });

      return { ivpInstances, connections, serviceBoxes };
    };

    const ivpComponents = buildIvpComponents(ivpData.serviceTable);
    const hasIvpAU1 = ivpComponents.ivpInstances.has('AU1');
    const hasIvpAU2 = ivpComponents.ivpInstances.has('AU2');
    const hasAnyIvp = ivpComponents.ivpInstances.size > 0;

    // ============================================================================
    // FUNCTION 13: START BUILDING MERMAID DIAGRAM
    // ============================================================================
    let mermaid = `%%{init: {'flowchart': {'curve': 'linear'}} }%%
flowchart LR

%% FLOWS
`;

    // Add IVP connections
    if (hasAnyIvp) {
      mermaid += `ivp_spacer\n`;
      ivpComponents.connections.forEach(conn => {
        mermaid += `${conn.from} --"${conn.label}"--> ${conn.to}\n`;
      });
    }

    if (showGen1Customer) {
      mermaid += `SYD1 --"${sessagen1}"--> Gen1Cust
SYD2 --"${sessagen2}"--> Gen1Cust
`;
    }

    // Only show carrier to SBC connections if Gen1 exists
    if (showGen1Customer) {
      mermaid += `carrier --> SYD1
carrier --> SYD2
`;
    }

    // Add carrier to IVP connection if carriages exist
    if (hasIvpCarriages && hasAnyIvp) {
      const carriageLabel = ivpData.carriages.map(c => c.name).join('<br/>');
      mermaid += `carrier --"${carriageLabel}"--> ivpgrp
`;
    }

    // ============================================================================
    // FUNCTION 14: DEFINE NODES - LEFT SIDE (CARRIERS)
    // ============================================================================
    mermaid += `
%% LEFT
carrier["${carriers}"]

`;

    // ============================================================================
    // FUNCTION 15: DEFINE NODES - CENTER (IVP REGIONS)
    // ============================================================================
    if (hasAnyIvp) {
      mermaid += `%% CENTER: IVP REGIONS (top)
subgraph ivpgrp["IVP Regions"]
direction TB
ivp_spacer(( )):::invis
`;

      if (hasIvpAU1) {
        mermaid += `IVPAU1["IVP - AU1"]\n`;
      }
      if (hasIvpAU2) {
        mermaid += `IVPAU2["IVP - AU2"]\n`;
      }

      if (hasBackbone && showGen1Customer) {
        mermaid += `ivp_anchor(( )):::invis\n`;
      }

      mermaid += `end

`;
    }

    // ============================================================================
    // FUNCTION 16: DEFINE NODES - CENTER (DATA-CENTRE SBCs) - GEN1 ONLY
    // ============================================================================
    if (showGen1Customer) {
      mermaid += `%% CENTER: DATA-CENTRE SBCs (middle + Sema)
subgraph dcs["Data-Centre SBCs"]
direction TB
`;

      if (usesSema) {
        mermaid += `Sema["Sema"]\n`;
      }

      mermaid += `SYD1["SYD1 Data-Centre SBC"]
SYD2["SYD2 Data-Centre SBC"]
`;

      if (hasAnyIvp && hasBackbone) {
        mermaid += `dcs_anchor(( )):::invis\n`;
      }

      mermaid += `end

`;

      if (hasAnyIvp && hasBackbone) {
        mermaid += `%% backbone between groups
dcs_anchor ---|"Backbone Trunks"| ivp_anchor

`;
      }
    }

    // ============================================================================
    // FUNCTION 17: DEFINE NODES - RIGHT SIDE (CUSTOMER BOXES)
    // ============================================================================
    mermaid += `%% RIGHT: CUSTOMERS
subgraph customers["Customers"]
direction TB
`;

    // Add IVP customer service boxes
    ivpComponents.serviceBoxes.forEach(box => {
      mermaid += `${box.id}["${box.label}"]\n`;
    });

    if (showGen1Customer) {
      mermaid += `Gen1Cust["${customerName}<br/>(${accountNo})"]\n`;
    }

    mermaid += `end

`;

    // ============================================================================
    // FUNCTION 18: APPLY CSS CLASSES TO NODES
    // ============================================================================
    mermaid += `%% STYLING
classDef cloud fill:#eef6ff,stroke:#0b5aa6,stroke-width:2px,color:#0b2b4c;
classDef dc    fill:#e8f0ff,stroke:#0b2b4c,stroke-width:2px,color:#0b2b4c;
classDef box   fill:#f7f7f9,stroke:#0b2b4c,stroke-width:1.5px,color:#0b2b4c;
classDef invis fill:transparent,stroke:transparent,color:transparent;
classDef smallText font-size:11px;

`;

    if (hasAnyIvp) {
      mermaid += `class IVPAU1,IVPAU2 cloud;\n`;
    }

    if (showGen1Customer) {
      const dcClasses = usesSema ? 'SYD1,SYD2,Sema' : 'SYD1,SYD2';
      mermaid += `class ${dcClasses} dc;\n`;
    }

    const boxClasses = ['carrier'];
    boxClasses.push(...ivpComponents.serviceBoxes.map(b => b.id));
    if (showGen1Customer) boxClasses.push('Gen1Cust');

    mermaid += `class ${boxClasses.join(',')} box;\n\n`;

    // ============================================================================
    // FUNCTION 19: APPLY STYLING TO SUBGRAPHS
    // ============================================================================
    if (hasAnyIvp) {
      mermaid += `style ivpgrp    fill:#ffffff,stroke:#ff6a3d,stroke-width:2px,color:#ff6a3d\n`;
    }

    if (showGen1Customer) {
      mermaid += `style dcs       fill:#ffffff,stroke:#ff6a3d,stroke-width:2px,color:#ff6a3d
`;
    }

    mermaid += `style customers fill:#ffffff,stroke:#ff6a3d,stroke-width:2px,color:#ff6a3d
`;

    // ============================================================================
    // FUNCTION 20: APPLY STYLING TO CONNECTION LINES (LINKS)
    // ============================================================================
    mermaid += `\n%% Reduce font size on all connecting line labels\n`;

    let linkCounter = 0;

    const totalConnections = ivpComponents.connections.length;
    const gen1Links = showGen1Customer ? 4 : 0;
    const carriageLinks = (hasIvpCarriages && hasAnyIvp) ? 1 : 0;

    for (let i = 0; i < totalConnections + gen1Links + carriageLinks; i++) {
      mermaid += `linkStyle ${linkCounter++} font-size:11px;\n`;
    }

    if (hasAnyIvp && hasBackbone && showGen1Customer) {
      mermaid += `\n%% Make the backbone line thick and solid\nlinkStyle ${linkCounter} stroke:#009966,stroke-width:5px\n`;
    }

    // ============================================================================
    // FUNCTION 21: RETURN COMPLETED MERMAID DIAGRAM
    // ============================================================================
    return mermaid;
  })()
}}
