Object.entries(
  (Get_Services.data.result || []).reduce((acc, s) => {
    if ((s.state || "").toString() !== "Active") return acc;

    // normalize profile -> flat array of objects
    let prof = s.profile ?? s.Profile ?? [];
    for (let i = 0; i < 2; i++) {
      if (typeof prof === "string") { try { prof = JSON.parse(prof); } catch(e) { break; } }
    }
    let flat = Array.isArray(prof)
      ? (prof.flat ? prof.flat() : prof.reduce((a,b)=>a.concat(b), []))
      : (prof && typeof prof === "object" ? [prof] : []);

    // collect unique names for this service
    const names = new Set();
    flat.forEach(p => {
      const nm = ((p?.name ?? "") + "").trim();
      if (nm) names.add(nm);
    });

    if (names.size === 0) {
      acc["VoiceGateways"] = (acc["VoiceGateways"] || 0) + 1;
    } else {
      names.forEach(nm => acc[nm] = (acc[nm] || 0) + 1);
    }
    return acc;
  }, {})
).map(([k,v]) => `${k}: ${v}`).join('\n')
}}
