{{ ((chartType) => ( 
(() => {
  const rows = statpull.data || [];
  const sel = Select1?.selectedOptionValue;

  // filter by selected service_id (or show all if empty/ALL)
  const filtered = rows.filter(r => !sel || sel === 'ALL' || r.service_id === sel);

  // unique, sorted dates for x-axis
  const dates = [...new Set(filtered.map(r => r.collection_date))].sort();

  // group by service_id; keep a nice label
  const bySvc = {};
  for (const r of filtered) {
    if (!bySvc[r.service_id]) {
      bySvc[r.service_id] = {
        label: `${r.external_id} — ${r.description}`,
        byDate: {}
      };
    }
    const sum24 = (pfx) =>
      Array.from({ length: 24 }, (_, i) => Number(r[`${pfx}_${i}`] ?? 0))
        .reduce((a, b) => a + b, 0);

    bySvc[r.service_id].byDate[r.collection_date] = {
      inbound: sum24('inbound'),
      outbound: sum24('outbound'),
    };
  }

  // build series per service (two lines each)
  const series = [];
  for (const [svcId, obj] of Object.entries(bySvc)) {
    const inbound = dates.map(d => obj.byDate[d]?.inbound ?? 0);
    const outbound = dates.map(d => obj.byDate[d]?.outbound ?? 0);

    series.push({
      name: `${obj.label} — inbound`,
      type: 'line',
      smooth: true,
      data: inbound,
    });
    series.push({
      name: `${obj.label} — outbound`,
      type: 'line',
      smooth: true,
      data: outbound,
    });
  }

  return {
    tooltip: { trigger: 'axis' },
    legend: { type: 'scroll' },
    xAxis: { type: 'category', data: dates },
    yAxis: { type: 'value', name: 'Calls/day' },
    series,
  };
})()
))(Chart1.chartType) }}
