{{
  (() => {
    const content = getconfig.data?.[0]?.config_blob || '';

    const searchTerm = String(configSearch?.text ?? configSearch ?? '').trim();
    const lineSearchTerm = String(lineSearch?.text ?? lineSearch ?? '').trim();

    if (!searchTerm && !lineSearchTerm) {
      return content;
    }

    // Section search only
    if (searchTerm && !lineSearchTerm) {
      const startIndex = content.indexOf(searchTerm);
      if (startIndex === -1) return content;

      // Find where to start (beginning of line with the heading)
      let lineStart = startIndex;
      while (lineStart > 0 && content[lineStart - 1] !== '\n') {
        lineStart--;
      }

      // Find the end (next heading or end of content)
      const afterTarget = content.slice(startIndex + searchTerm.length);
      const nextHeadingMatch = afterTarget.match(/\n#{1,6}\s/);

      const endIndex = nextHeadingMatch 
        ? startIndex + searchTerm.length + nextHeadingMatch.index
        : content.length;

      // Return the slice from the start of the heading line
      return content.slice(lineStart, endIndex).trim();
    }

    // Line search (rest of the code remains the same)
    const lines = content.split('\n');
    const result = [];
    let currentSection = '';
    let lastAddedSection = '';
    let inTargetSection = !searchTerm;
    let currentParentLine = '';
    let lastAddedParent = '';
    let includeFollowingIndented = false;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];

      if (line.match(/^#{1,6}\s/)) {
        currentSection = line;
        currentParentLine = '';
        includeFollowingIndented = false;
        if (searchTerm) {
          inTargetSection = line.includes(searchTerm);
        }
      }

      if (searchTerm && !inTargetSection) continue;

      if (line && !line.startsWith(' ') && !line.startsWith('```') && !line.match(/^#{1,6}\s/)) {
        currentParentLine = line;
        includeFollowingIndented = false;
      }

      const isMatch = line.toLowerCase().includes(lineSearchTerm.toLowerCase()) && 
                      !line.match(/^#{1,6}\s/) && !line.startsWith('```');
      const isIndented = line.startsWith(' ');

      if (isMatch || (isIndented && includeFollowingIndented)) {

        if (currentSection && currentSection !== lastAddedSection) {
          if (result.length > 0 && result[result.length - 1] !== '```') {
            result.push('```');
          }
          if (result.length > 0) result.push('');
          result.push(currentSection);
          result.push('```text');
          lastAddedSection = currentSection;
          lastAddedParent = '';
        }

        if (isIndented && currentParentLine && currentParentLine !== lastAddedParent) {
          result.push(currentParentLine);
          lastAddedParent = currentParentLine;
          includeFollowingIndented = true;
        }
        else if (!isIndented && isMatch) {
          if (line !== lastAddedParent) {
            result.push(line);
            lastAddedParent = line;
          }
          includeFollowingIndented = true;
        }
        else if (isIndented) {
          result.push(line);
        }
      } else if (!isIndented) {
        includeFollowingIndented = false;
      }
    }

    if (result.length > 0 && result[result.length - 1] !== '```') {
      result.push('```');
    }

    return result.length > 0 ? result.join('\n') : content;
  })()
}}
