{{
  (() => {
    const content = getconfig.data?.[0]?.config_blob || '';
    const searchTerm = configSearch.text?.trim();

    // If no search term, show all content
    if (!searchTerm) return content;

    // Find the target heading
    const startIndex = content.indexOf(searchTerm);
    if (startIndex === -1) return '';

    // Find the next heading after target
    const afterTarget = content.slice(startIndex + searchTerm.length);
    const nextHeadingMatch = afterTarget.match(/\n#{1,6}\s/);

    // Extract content between headings
    const endIndex = nextHeadingMatch 
      ? startIndex + searchTerm.length + nextHeadingMatch.index
      : content.length;

    return content.slice(startIndex, endIndex).trim();
  })()
}}
